import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import PopupState, { bindTrigger, bindMenu } from "material-ui-popup-state";

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

function Correspondence() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button variant="outlined" onClick={handleClickOpen}>
        View Correspondence
      </Button>
      <Dialog
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <div style={{ display: "flex" }}>
          <DialogTitle id="customized-dialog-title" onClose={handleClose}>
            View Correspondence
          </DialogTitle>
          <DialogTitle id="customized-dialog-title" onClose={handleClose}>
            <div style={{ display: "flex", marginLeft:"110px"}}>
              <div style={{fontSize:"15px", paddingRight:'10px'}}>View</div>
              <PopupState variant="popover" popupId="demo-popup-menu" style={{paddingLeft :"15px"}}>
                {(popupState) => (
                  <React.Fragment>
                    <Button
                      variant="contained"
                      color="primary"
                      {...bindTrigger(popupState)}
                    >
                      Template 1
                    </Button>
                    <Menu {...bindMenu(popupState)}>
                      <MenuItem onClick={popupState.close}>Template 2</MenuItem>
                    </Menu>
                  </React.Fragment>
                )}
              </PopupState>
            </div>
          </DialogTitle>
        </div>
        <DialogContent dividers>
            <small style={{color:"", letterSpacing: "0.36px", paddingLeft: "5px",textAlign: "left",
font: "normal normal normal 20px Ubuntu"}}><div style={{marginTop:"0px"}}>Subject<span style={{color:""}}>INVOICE Details - (Account Number)</span><br /></div>
                Dear sir/Madam,<br />
                Greetings!! <br />
                    This is to remind you that there are one or more open invoices on your account. Lease provide at your earliest convenience an update on thr payment details or clarify the reason <br />
                    for this delay . If you have an specific issue with the invoice(s). Please let us know so that we can address it to the correct department.
                    Please find the details of the invoices below: 
                    {/* {<Table2 />} */}
                <br />Total Amount to be Paid :K <br />
                <small> In case you have already made a payment for the above items, please send us the details to ensure the payment is posted <br />
                Let us know if we can be of any further assisstance. Looking forward to hearing from you</small> 
            </small>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="primary">
            Save changes
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
export default Correspondence;
