import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import EditIcon from "@material-ui/icons/Edit";
const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

class EditModal extends React.Component {
  state = {
    formData: {
      inv_amt: "",
      notes: "",
    },
    open: false,
  };
  handleChange = (event) => {
    console.log(event.target.value);
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({ formData });
  };
  // const [, setOpen] = React.useState(false);

  handleClickOpen = () => {
    this.setState({ open: true });
  };
  handleClose = () => {
    this.setState({ open: true });
  };
  render() {
    return (
      <div>
        <Button
          startIcon={<EditIcon />}
          variant="outlined"
          onClick={this.handleClickOpen}
        >
          Edit
        </Button>
        <Dialog
          onClose={this.handleClose}
          aria-labelledby="customized-dialog-title"
          open={open}
          style={{ backgroundColor: "transparent" }}
        >
          <DialogTitle id="customized-dialog-title" onClose={this.handleClose}>
            Edit Invoice
          </DialogTitle>
          <DialogContent dividers>
            <div>
              <ValidatorForm
                ref="form"
                style={{ padding: "5px", display: "flex" }}
              >
                <div style={{ display: "block" }}>
                  <div style={{ display: "flex" }}>
                    <span style={{ fontSize: "15px", paddingRight: "10px" }}>
                      Invoice Amount
                    </span>
                    <TextValidator
                      variant="outlined"
                      size="small"
                      onChange={this.handleChange}
                      name="inv_amt"
                      value={formData.inv_amt}
                      validators={["required", "isNumber"]}
                      errorMessages={[
                        "this field is required",
                        "Please Enter valid Amount",
                      ]}
                    />
                  </div>
                  <div style={{ display: "flex", paddingTop: "20px" }}>
                    <span style={{ fontSize: "15px", paddingRight: "70px" }}>
                      Notes:
                    </span>
                    <TextareaAutosize
                      id="outlined-basic"
                      label="Notes"
                      variant="outlined"
                      rowsMin={10}
                      style={{ padding: "20px", display: "block" }}
                      onChange={this.handleChange}
                    />
                  </div>
                </div>
              </ValidatorForm>
            </div>
          </DialogContent>
          <DialogActions>
            <Button
              autoFocus
              onClick={this.handleClose}
              color="primary"
              style={{ color: "#14AFF1", marginRight: "13vw" }}
            >
              Cancel
            </Button>
            <Button autoFocus color="primary">
              Reset
            </Button>
            <Button
              color="primary"
              variant="contained"
              typ
              style={{ backgroundColor: "#14AFF1" }}
              onClick={this.handleSubmit}
            >
              Apply
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}
export default EditModal;
