import React from "react";
import { ValidatorForm, TextValidator } from "react-material-ui-form-validator";
import { TextareaAutosize } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import DateFnsUtils from "@date-io/date-fns";
import "date-fns";
import Snackbar from "@material-ui/core/snackbar";
import InvoiceTable from "./table";
import Button from "@material-ui/core/Button";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";

function MaterialUIPickers({ date, handleDate }) {
  const [selectedDate, setSelectedDate] = React.useState(
    new Date("2014-08-18")
  );

  const handleDateChange = (d) => {
    setSelectedDate(d);
    date = selectedDate;
    handleDate(date);
  };
  return (
    <MuiPickersUtilsProvider utils={DateFnsUtils}>
      <Grid container justify="space-around">
        <KeyboardDatePicker
          variant="inline"
          format="MM/dd/yyyy"
          margin="normal"
          id="date-picker-inline"
          name="selectedDate"
          value={selectedDate}
          onChange={handleDateChange}
          KeyboardButtonProps={{
            "aria-label": "change date",
          }}
        />
      </Grid>
    </MuiPickersUtilsProvider>
  );
}
class AddForm extends React.Component {
  state = {
      cust_name: "",
      cust_no: "",
      inv_no: "",
      inv_amt: "",
      date: "",
      notes: "",
      snackbaropen: false,
      snackbarmsg: "",
  };

  handleDate = (d) => {
    console.log(d);
    let v= d.toString();
    this.setState({date: v });
    console.log(this.state.formData);
  };

  handleChange = (btn) => {
    handleBtn
  };

  handleSubmit = (e) => {
    console.log(e.target.value);
    this.setState({ submitted: true }, () => {
      setTimeout(() => this.setState({ submitted: false }), 5000);
    });
  };

  snackbarClose = (e) => {
    this.setState({ snackbaropen: false });
  };
  
  render() {
    const {btn, handleBtn}= this.props;
    return (
      <div>
        <Snackbar
          anchorOrigin={{ vertical: "bottom", horizontal: "çenter" }}
          open={this.state.snackbaropen}
          autoHideDuration={3000}
          onClose={this.snackbarClose}
          message={<span id="msg-id">{this.state.snackbarmsg}</span>}
        />
        <ValidatorForm
          ref="form"
          onSubmit={this.handleSubmit}
          style={{ padding: "5px", display: "flex" }}
        >
          <div style={{ padding: "5px", display: "block" }}>
            <div style={{ display: "flex" }}>
              <span style={{ fontSize: "15px", padding: "5px" }}>
                {" "}
                Customer Name
              </span>
              <TextValidator
                onChange={this.handleChange}
                size="small"
                variant="outlined"
                name="cust_name"
                validators={["required", "isString"]}
                errorMessages={["this field is required", "Please Enter Name"]}
              />
            </div>
            <div style={{ display: "flex", padding: "5px" }}>
              <span style={{ fontSize: "15px" }}> Customer Number</span>
              <TextValidator
                onChange={this.handleChange}
                variant="outlined"
                size="small"
                name="cust_no"
                validators={["required", "isNumber"]}
                errorMessages={[
                  "this field is required",
                  "Please Enter Customer Number",
                ]}
              />
            </div>
            <div style={{ display: "flex", padding: "5px" }}>
              <span style={{ fintSize: "15px" }}> Invoice Number</span>
              <TextValidator
                variant="outlined"
                size="small"
                onChange={this.handleChange}
                name="inv_no"
                validators={["required", "isNumber"]}
                errorMessages={[
                  "this field is required",
                  "Please Valid Invoice  Number",
                ]}
              />
            </div>
            <div style={{ display: "flex", padding: "5px" }}>
              <span style={{ fontSize: "15px" }}>Invoice Amount</span>
              <TextValidator
                variant="outlined"
                size="small"
                onChange={this.handleChange}
                name="inv_amt"
                validators={["required", "isNumber", "isFloat"]}
                errorMessages={[
                  "this field is required",
                  "Please Amount  Number",
                ]}
              />
            </div>
          </div>
          <div style={{ paddingLeft: "20px", display: "block" }}>
            <div style={{ display: "flex", padding: "5px" }}>
              <span style={{ fontSize: "15px" }}>Date Picker</span>
              <MaterialUIPickers
                name="date"
                date={this.state.date}
                handleDate={this.handleDate}
              />
            </div>
            <div style={{ paddingLeft: "20px", display: "block" }}>
              <span style={{ fontSize: "15px" }}>Notes: </span>
              <TextareaAutosize
                id="outlined-basic"
                label="Notes"
                variant="outlined"
                rowsMin={10}
                name="notes"
                onChange={this.handleChange}
              />
            </div>
          </div>
        </ValidatorForm>
      </div>
    );
  }
}
export default AddForm;
