import React from 'react';
import Button from '@material-ui/core/Button';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { TextareaAutosize } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

 function DatePickers() {
  const classes = useStyles();

  return (
    <form className={classes.container} validate>
      <TextField
        id="date"
        label="Due Date"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      /> 
    </form>
  );
}


export default class SimpleFormExample extends React.Component {
        state = {
        formData : { 
                    cust_name: '',
                    cust_no:'',
                    inv_no: '',
                    inv_amt:'',
                    date:''
                 },
        submitted: false,
    }

    handleChange = (event) => {
      console.log(event.target.value);
        const { formData } = this.state;
        formData[event.target.name] = event.target.value;
        this.setState({ formData });
    }

    handleSubmit = (e) => {
        // this.setState({ submitted: true }, () => {
        //     setTimeout(() => this.setState({ submitted: false }), 5000);
        // });
    }

    render() {
        const { formData, submitted } = this.state;
        return (
            <div>
              <ValidatorForm
                ref="form"
                onSubmit={this.handleSubmit} style={{padding:'5px', display: "flex"}}
            >
               <div  style={{padding:'5px', display: "block"}}>
                <TextValidator
                      label="Customer Name"
                      onChange={this.handleChange}
                      name="cust_name"
                      value={formData.cust_name}
                      validators={['required', 'isString']}
                      errorMessages={['this field is required', 'Please Enter Name']}
                  />
                  <TextValidator
                      label="Customer Number"
                      onChange={this.handleChange}
                      name="cust_no"
                      value={formData.cust_no}
                      validators={['required' , 'isNumber']}
                      errorMessages={['this field is required', 'Please Customer Number']}
                  />
                  <TextValidator
                      label="Invoice Number"
                      onChange={this.handleChange}
                      name="inv_no"
                      value={formData.inv_no}
                      validators={['required' , 'isNumber']}
                      errorMessages={['this field is required', 'Please Valid Invoice  Number']}
                  />
                  <TextValidator
                      label="Invoice Amount"
                      onChange={this.handleChange}
                      name="inv_amt"
                      value={formData.inv_amt}
                      validators={['required' , 'isNumber','isFloat']}
                      errorMessages={['this field is required', 'Please Amount  Number']}
                  />
               </div>
                <div style={{padding:'5px', display: "block"}}>
                <DatePickers  
                 onChange={this.handleChange} />
                  Notes: <TextareaAutosize id="outlined-basic" label="Notes" variant="outlined" rowsMin={10}  />
                </div>
            </ValidatorForm>
                <Button
                    color="primary"
                    variant="contained"
                    type="submit"
                    disabled={submitted}
                >
                    {
                        (submitted && 'Your Invoice is Added!')
                        || (!submitted && 'Submit')
                    }
                </Button>
            </div>
        );
    }
}
