import React  from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import "../utils/table.css";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { CircularProgress, Typography, Check, TextField, Grid } from "@material-ui/core";
import InfiniteScroll from "react-infinite-scroll-component";
// import { ReactComponent as CheckBox } from "../assets/check_box_outline_blank-black-18dp.svg";
// import { ReactComponent as TickBox } from "../assets/check_box-black-18dp.svg";
import axios from "axios";
import { useState, useEffect } from "react";
import Checkbox from "@material-ui/core/Checkbox";
// import Search from './Search';


const useStyles = makeStyles({
  "@global": {
    "*::-webkit-scrollbar": {
      width: "0.4em",
    },
    "*::-webkit-scrollbar-track": {
      "-webkit-box-shadow": "inset 0 0 6px rgba(0,0,0,0.00)",
    },
    "*::-webkit-scrollbar-thumb": {
      backgroundColor: "rgba(97, 112, 123, 1)",
      outline: "1px solid slategrey",
    },
  },
  table: {
    // width: "85vw",
    // // margin: '0px 30px',
    justifyContent: 'center',
    fontSize: "0.1em", 
  },
  Paper: {
    width: "100vw",
    // margin: "1em",
    height: "80vh",
    background: "#273D49CC",
  },
  TableHead: {
    borderBottom: "0.01px solid black",
    textAlign: "right",
    height:"1vh",
    font: "normal normal normal 18px/21px Ubuntu",
    color: "#97A1A9",
    textTransform: "capitalize",
    backgroundColor: "#273D49CC",
  },

  TableRows: {
    "&.tr $nth-child(even)": "background-color: #283A46",
    "&.tr $nth-child(odd)": 'backgroundColor: "#273D49CC",',
  },

  TableRow: {
    border: "none",
    textAlign: "center",
    font: "normal normal normal 20px/24px Ubuntu",
    color: "#FFFFFF",
    height:"1vh",
  },
});

function Table2({ rows }) {
  const classes= useStyles();
  const {data, setData}= useState([]);
  useEffect( () => {fetchData(); } , []);
  const fetchData = async () =>  {
    await axios({
      method: "GET",
      url: "http://localhost:8080/Summer_Internship_Backend/Get_invoice",
    })
      .then((response) => {
        setData([...data, ...response.data]);    
        isNextFunc(true);
        console.log(data);  
      })
      .catch((error) => {
        console.log(error);
      });
  };
  return (
    <TableContainer id="Target" className={classes.Paper}>
      <Table
        classes={{ root: classes.table }}
        aria-label="sticky table"
      >
        <TableHead>
          <TableRow>
            <TableCell classes={{ root: classes.TableHead }}>
              <Typography>Invoice Number</Typography>
            </TableCell>
            <TableCell align="right" classes={{ root: classes.TableHead }}>
              <Typography>PO Number</Typography>
            </TableCell>
            <TableCell align="right" classes={{ root: classes.TableHead }}>
              <Typography> Invoice Date</Typography>
            </TableCell>
            <TableCell align="right" classes={{ root: classes.TableHead }}>
              <Typography> Due Date</Typography>
            </TableCell>
            <TableCell align="right" classes={{ root: classes.TableHead }}>
              <Typography> Currency</Typography>
            </TableCell>
            <TableCell align="right" classes={{ root: classes.TableHead }}>
              <Typography>Open Amount&nbsp;($)</Typography>
            </TableCell>
          </TableRow>
        </TableHead>

        <TableBody className="body">
          {data.map((row, index) => (
            <TableRow
              className={classes.TableRow}>
              <TableCell
                component="th"
                scope="row"
                className={classes.TableRow}
              >
                <Typography> {row.inv_no} </Typography>
              </TableCell>
              <TableCell align="right" className={classes.TableRow}>
                <Typography>{row.po_no}</Typography>
              </TableCell>
              <TableCell align="right" className={classes.TableRow}>
                <Typography> {row.inv_date}</Typography>
              </TableCell>
              <TableCell align="right" className={classes.TableRow}>
                <Typography>{row.curr}</Typography>
              </TableCell>
              <TableCell align="right" className={classes.TableRow}>
                <Typography>{row.opn_amt}</Typography>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
export default Table2;
