import React from "react";
import axios from "axios";
import clsx from "clsx";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import CircularProgress from "@material-ui/core/CircularProgress";
import Checkbox from "@material-ui/core/Checkbox";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(even)": {
      backgroundColor: "#2A3E4C",
    },
  },
}))(TableRow);
// const useStyles = makeStyles({
//   table: {
//     minWidth: 700,
//   },
// });

const useStyles = makeStyles({
  root: {
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  icon: {
    borderRadius: 3,
    width: 16,
    height: 16,
    boxShadow:
      "inset 0 0 0 1px rgba(16,22,26,.2), inset 0 -1px 0 rgba(16,22,26,.1)",
    backgroundColor: "#f5f8fa",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.8),hsla(0,0%,100%,0))",
    "$root.Mui-focusVisible &": {
      outline: "2px auto rgba(19,124,189,.6)",
      outlineOffset: 2,
    },
    "input:hover ~ &": {
      backgroundColor: "#ebf1f5",
    },
  },
  checkedIcon: {
    backgroundColor: "#137cbd",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.1),hsla(0,0%,100%,0))",
    "&:before": {
      display: "block",
      width: 16,
      height: 16,
      backgroundImage:
        "url(\"data:image/svg+xml;charset=utf-8,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16'%3E%3Cpath" +
        " fill-rule='evenodd' clip-rule='evenodd' d='M12 5c-.28 0-.53.11-.71.29L7 9.59l-2.29-2.3a1.003 " +
        "1.003 0 00-1.42 1.42l3 3c.18.18.43.29.71.29s.53-.11.71-.29l5-5A1.003 1.003 0 0012 5z' fill='%23fff'/%3E%3C/svg%3E\")",
      content: '""',
    },
    "input:hover ~ &": {
      backgroundColor: "#106ba3",
    },
  },
});


const CheckBox = (props) => {
  const classes = useStyles();
  return (
    <Checkbox
      className={classes.root}
      disableRipple
      color="default"
      checkedIcon={<span className={clsx(classes.icon, classes.checkedIcon)} />}
      icon={<span className={classes.icon} />}
      inputProps={{ "aria-label": "decorative checkbox" }}
      {...props}
    />
  );
};

class InvoiceTable extends React.Component {

    constructor(props){
      super(props);
      this.state = {
        tableData: [],
        deleteKey:[],
        pageCount:0
      };
    }

  displayDelete=(value)=>{
    for(let i=0;i<this.state.deleteKey.length;i+=1){
      if(this.state.deleteKey[i]===value){
        const r=this.state.deleteKey
        r.splice(i,1)
        this.setState({deleteKey:r})
         this.props.handleD(this.state.deleteKey);
        // console.log(this.state.deleteKey);
        return; 
      }
    }
    const d=this.state.deleteKey
    d.push(value)
    this.setState({deleteKey:d})
    this.props.handleD(this.state.deleteKey);
    // console.log(d)
  }

  displayDeleteAll=()=>
  {
    const tableData = this.state.tableData;
      tableData.map(key =>{
      console.log(key.key);
    })
  }
  // componentDidUpdate(){
  //   this.props.handleD(this.state.deleteKey);
  // } 
  //   pageCount=()=>{
  //     this.setState({
  //       pageCount:this.state.pageCount+1 
  //     });
  //     setTimeout(() => {
  //       fetchData();
  //     }, 2000);
  //     // useEffect(pageCount);
  // }

  componentDidMount()
  {
    axios
      .get("http://localhost:8080/Summer_Internship_Backend/Get_invoice",{ params:{
        _limit: 10
      }
      })
      .then((response) => {
        console.log(response.data);
        const temp=[]
        for(let i=0;i<response.data.length;i+=1){
          temp.push({
            invoice_id:response.data[i].invoice_id,
            name_customer:response.data[i].name_customer,
            cust_number:response.data[i].cust_number,
            total_open_amount:response.data[i].total_open_amount,
            due_in_date:response.data[i].due_in_date,
          })
        }
        console.log(temp)
        this.setState({ tableData: temp });
      });
  }

  render() {
    const { tableData } = this.state;
    // console.log(this.props);
    // console.log("hello",this.props);
    return (
      <TableContainer component={Paper}>
        <Table aria-label="customized table">
          <TableHead>
            <TableRow>
              <TableCell>
                <CheckBox onChange={this.displayDeleteAll} />
              </TableCell>
              <StyledTableCell>Key</StyledTableCell>
              <StyledTableCell>resourceID</StyledTableCell>
              <StyledTableCell>resourceType</StyledTableCell>
              <StyledTableCell>tenantName</StyledTableCell>
              <StyledTableCell>dealerID</StyledTableCell>
              <StyledTableCell>status</StyledTableCell>
              <StyledTableCell>logFilePath</StyledTableCell>
              <StyledTableCell>supportPerson</StyledTableCell>
              <StyledTableCell>lastUpdatedTime</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {!tableData ? (
              <CircularProgress />
            ) : (
              tableData.map((row) => (
                <StyledTableRow>
                  <TableCell>
                    <CheckBox value={row.invoice_id} onChange={value=>this.displayDelete(value.target.value)} />
                  </TableCell>
                  <StyledTableCell>{row.name_customer}</StyledTableCell>
                  <StyledTableCell>{row.cust_number}</StyledTableCell>
                  <StyledTableCell>{row.invoice_id}</StyledTableCell>
                  <StyledTableCell>{row.total_open_amount}</StyledTableCell>
                  <StyledTableCell>{row.due_in_date}</StyledTableCell>
                  <StyledTableCell>--</StyledTableCell>
                  <StyledTableCell>--</StyledTableCell>
                  <StyledTableCell>--</StyledTableCell>
                </StyledTableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }
}
export default InvoiceTable;
