import React, { Component } from 'react';
import { FormControl ,InputLabel ,Input ,FormHelperText } from '@material-ui/core';
import { TextareaAutosize } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

 function DatePickers() {
  const classes = useStyles();

  return (
    <form className={classes.container} noValidate>
      <TextField
        id="date"
        label="Birthday"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
    </form>
  );
}


class AddForm extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            cust_name: '',
            cust_no:'',
            inv_no: '',
            inv_amt:''
         }
    }
    handlecustname =(e) =>{
        this.setState({
            cust_name: e.target.value
        })
    }
    handlecustno =(e) =>{
        this.setState({
            cust_no: e.target.value
        })
    }
    handleinvno =(e) =>{
        this.setState({
            inv_no: e.target.value
        })
    }
    handleinvamt =(e) =>{
        this.setState({
            inv_amt: e.target.value
        })
    }
    render() { 
        return (  
            < div style={{padding:'5px', display: "flex"}}>
               <div style={{padding:'5px', display: "block"}}>
                <FormControl>
                    <InputLabel htmlFor="my-input">Customer Name</InputLabel>
                    <Input id="my-input" aria-describedby="my-helper-text"  value={this.cust_name} onChange={this.handlecustname} required />
                    </FormControl>
                    <FormControl>
                    <InputLabel htmlFor="my-input">Customer Number</InputLabel>
                    <Input id="my-input" aria-describedby="my-helper-text" value={this.cust_no} onChange={this.handlecustno} required/>
                    <FormHelperText id="my-helper-text">We'll never share your Number</FormHelperText>
                    </FormControl>
                    <FormControl>
                    <InputLabel htmlFor="my-input">Invoice Number</InputLabel>
                    <Input id="my-input" aria-describedby="my-helper-text" value={this.inv_no} onChange={this.handleinvno} required/>
                    </FormControl>
                    <FormControl>
                    <InputLabel htmlFor="my-input">Invoice Amount</InputLabel>
                    <Input id="my-input" aria-describedby="my-helper-text" value={this.inv_amt} onChange={this.handleinvamt} required/>
                </FormControl>
               </div>
                <FormControl >
                    Notes <TextareaAutosize id="outlined-basic" label="Notes" variant="outlined" rowsMin={10}  />
                </FormControl>
                <FormControl >
                 Due Date : <DatePickers />
                </FormControl>
            </div>
        );
    }
}
 
export default AddForm;
