import React from 'react';
import Button from '@material-ui/core/Button';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

export default class SimpleFormExample extends React.Component {
        state = {
        formData : { 
                    cust_name: '',
                    cust_no:'',
                    inv_no: '',
                    inv_amt:''
                 },
        submitted: false,
    }

    handleChange = (event) => {
        const { formData } = this.state;
        formData[event.target.name] = event.target.value;
        this.setState({ formData });
    }

    handleSubmit = (e) => {
      console.log(e.target);
        this.setState({ submitted: true }, () => {
            setTimeout(() => this.setState({ submitted: false }), 5000);
        });
    }

    render() {
        const { formData, submitted } = this.state;
        return (
            <ValidatorForm
                ref="form"
                onSubmit={this.handleSubmit}
            >
                <h2>Simple form</h2>
                <TextValidator
                    label="Customer Name"
                    onChange={this.handleChange}
                    name="cust_name"
                    value={formData.cust_name}
                    validators={['required', 'isString']}
                    errorMessages={['this field is required', 'Please Enter Name']}
                />
                <TextValidator
                    label="Customer Number"
                    onChange={this.handleChange}
                    name="cust_no"
                    value={formData.cust_no}
                    validators={['required' , 'isNumber']}
                    errorMessages={['this field is required', 'Please Customer Number']}
                />
                <TextValidator
                    label="Invoice Number"
                    onChange={this.handleChange}
                    name="inv_no"
                    value={formData.inv_no}
                    validators={['required' , 'isNumber']}
                    errorMessages={['this field is required', 'Please Valid Invoice  Number']}
                />
                <TextValidator
                    label="Invoice Amount"
                    onChange={this.handleChange}
                    name="inv_amt"
                    value={formData.inv_amt}
                    validators={['required' , 'isNumber','isFloat']}
                    errorMessages={['this field is required', 'Please Amount  Number']}
                />
                <Button
                    color="primary"
                    variant="contained"
                    type="submit"
                    disabled={submitted}
                >
                    {
                        (submitted && 'Your Invoice is Added!')
                        || (!submitted && 'Submit')
                    }
                </Button>
            </ValidatorForm>
        );
    }
}
