import React, { Component } from 'react';
import { Form } from 'reactstrap';
class AddForm extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            cust_name: '',
            cust_no:'',
            inv_no: '',
            inv_amt:''
         }
    }
    handlecustname =(e) =>{
        this.setState({
            cust_name: e.target.value
        })
    }
    handlecustno =(e) =>{
        this.setState({
            cust_no: e.target.value
        })
    }
    handleinvno =(e) =>{
        this.setState({
            inv_no: e.target.value
        })
    }
    handleinvamt =(e) =>{
        this.setState({
            inv_amt: e.target.value
        })
    }
    render() { 
        return (  
            <form>
                <div className="form-group">
                    <label>
                        Customer Name
                    </label>
                    <input type="text" className="form-control"  aria-describedby="emailHelp" value={this.cust_name} onChange={this.handlecustname}/>
                    <label>
                        Customer number
                    </label>
                    <input type="number" className="form-control" aria-describedby="emailHelp" value={this.cust_no} onChange={this.handlecustno} />
                    <label>
                        Invoice Number
                    </label>
                    <input type="number" className="form-control"  aria-describedby="emailHelp"  value={this.inv_no} onChange={this.handleinvno} />
                    <label>
                        Invoice Amount
                    </label>
                    <input type="number" className="form-control"  aria-describedby="emailHelp"  value={this.inv_amt} onChange={this.handleinvamt} />
                </div>
            </form>
        );
    }
}
 
export default AddForm;
