import React, { Component } from 'react';
import { FormControl ,InputLabel ,Input ,FormHelperText } from '@material-ui/core';
import { TextareaAutosize } from '@material-ui/core';
class AddForm extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            cust_name: '',
            cust_no:'',
            inv_no: '',
            inv_amt:''
         }
    }
    handlecustname =(e) =>{
        this.setState({
            cust_name: e.target.value
        })
    }
    handlecustno =(e) =>{
        this.setState({
            cust_no: e.target.value
        })
    }
    handleinvno =(e) =>{
        this.setState({
            inv_no: e.target.value
        })
    }
    handleinvamt =(e) =>{
        this.setState({
            inv_amt: e.target.value
        })
    }
    render() { 
        return (  
            <>
                <FormControl>
                <InputLabel htmlFor="my-input">Customer Name</InputLabel>
                <Input id="my-input" aria-describedby="my-helper-text"  value={this.cust_name} onChange={this.handlecustname} />
                </FormControl>
                <FormControl>
                <InputLabel htmlFor="my-input">Customer Number</InputLabel>
                <Input id="my-input" aria-describedby="my-helper-text" value={this.cust_no} onChange={this.handlecustno}/>
                <FormHelperText id="my-helper-text">We'll never share your Number</FormHelperText>
                </FormControl>
                <FormControl>
                <InputLabel htmlFor="my-input">Invoice Number</InputLabel>
                <Input id="my-input" aria-describedby="my-helper-text" value={this.inv_no} onChange={this.handleinvno}/>
                </FormControl>
                <FormControl>
                    <TextareaAutosize rowsMax="5" />
                </FormControl>
                <FormControl>
                <InputLabel htmlFor="my-input">Invoice Amount</InputLabel>
                <Input id="my-input" aria-describedby="my-helper-text" value={this.inv_amt} onChange={this.handleinvamt}/>
                </FormControl>
            </>
        );
    }
}
 
export default AddForm;
