import React from "react";
import { Typography, AppBar, Toolbar,Button, Paper } from "@material-ui/core";
import CustomizedDialogs from "./practice";
import EditModal from "./editmodal";
import EditForm from "./editForm";
import DeleteModal from "./deleteModal";
import Correspondence from "./correspondence";

class Header3 extends React.Component {
    state={
      rows: [],
      btn:true,
    };
    static getDerivedStateFromProps(props, state){
      console.log(props.data, state);
      return{
        rows:props.data
      }
    }
    tick() {
      if(this.state.rows.length> 0)
      {
        this.setState({btn:false});
        console.log('aa raha hai');
      }
      else
      {
        this.setState({btn:true});
        console.log(' ni aa raha hai');
      }
    }
  render(){
    console.log(this.state.btn);
    return (
      <Paper>
            <Toolbar>
            <Typography variant="h6" style={{ flexGrow: 1, display:"flex"}}>
              <Button variant="outlined" disabled={this.state.btn}>
                Predict
              </Button>
              <>
                <Correspondence rows={this.state.rows} length={this.state.rows.length} />
              </>
            </Typography>
            <>
              <CustomizedDialogs>
              </CustomizedDialogs>
            </>
            <>
              <EditModal rows={this.state.rows} btn={this.state.btn}>
              </EditModal>
            </>
            <>
              <DeleteModal rows={this.state.rows} btn={this.state.btn} />
            </>
            <>
              {/* <TextField  onChange={handleSearchValueChange} variant="outlined" name="search" placeholder="Search by Bill Number" style={{height: '2vh'}}><SearchIcon /></TextField> */}
            </>
          </Toolbar>
        </Paper>
    );
  }
}
export default Header3;
